def calculate_statistics(numbers: list) -> dict:
    """Calculate basic statistics for a list of numbers."""
    if not numbers:
        return {'mean': 0, 'sum': 0, 'count': 0}

    total = sum(numbers)
    count = len(numbers)
    mean = total / count

    return {
        'mean': mean,
        'sum': total,
        'count': count
    }
